import React from 'react';
import { StyleSheet, ScrollView, Text, View, Image, Button, Linking, Pressable } from 'react-native';

const DetailScreen = ({ route }) => {
  const { title, 
    author,
    price,
    score,
    url,
    image,
    description
  } = route.params;
  return (
    <ScrollView>
      <View style={styles.container}>
        <Image
          style={styles.image}
          source={{
            uri: image
          }}
        />
        <Text style={styles.h1}>{title}</Text>
        <Text style={[styles.b1, styles.gray]}>{author}</Text>
        <Text style={[styles.b1, styles.gray]}>
          <Text style={{color:'#000'}}>{score} </Text>
          / 5.0
        </Text>
        <Text style={[styles.b1]}>{description}</Text>
        <Pressable
          onPress={() => Linking.openURL(url)}
          style={styles.bt}>
            <Text style={[styles.btText, styles.h2]}>BUY NOW FOR ${price}</Text>
        </Pressable>
      </View>
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  container:{
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  image: {
    height: 300,
    width: 210,
  },
  bt: {
    alignItems: 'center',
    justifyContent: 'center',
    paddingVertical: 10,
    paddingHorizontal: 16,
    borderRadius: 4,
    elevation: 3,
    backgroundColor: '#6200EE',
  },
  btText:{
    color: 'white',
  },
  h1:{
    fontFamily: 'Roboto',
    fontSize: 24,
    lineHeight: 28,
    fontWeight: '500',
    letterSpacing: 0.3,
  },
  h2:{
    fontFamily: 'Roboto',
    fontSize: 16,
    lineHeight: 18,
    fontWeight: '500',
    letterSpacing: 0.012,
  },
  b1:{
    fontFamily: 'Roboto',
    fontSize: 14,
    lineHeight: 16,
    fontWeight: '400',
    letterSpacing: 0.012,
  },
  b2:{
    fontFamily: 'Roboto',
    fontSize: 12,
    lineHeight: 14,
    fontWeight: '400',
  },
  gray:{
    color: '#666666',
  }
});

export default DetailScreen;
